// src/components/App.js
import React, { useState } from 'react';
import VinylPlayer from './VinylPlayer';
import MusicService from './MusicService';

const App = () => {
  const [service, setService] = useState(null);
  const [trackList, setTrackList] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  const changeTrack = (direction) => {
    if (direction === 'next') {
      setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % trackList.length);
    } else if (direction === 'prev') {
      setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + trackList.length) % trackList.length);
    }
  };

  const currentTrack = trackList[currentTrackIndex];

  return (
    <div className="app">
      <button onClick={() => setService(service === 'spotify' ? 'apple' : 'spotify')}>
        Switch to {service === 'spotify' ? 'Apple Music' : 'Spotify'}
      </button>
      {service && <MusicService service={service} setTrackList={setTrackList} />}
      {currentTrack && <VinylPlayer track={currentTrack} service={service} changeTrack={changeTrack} />}
    </div>
  );
};

export default App;
